import React from "react";
import BotCard from "./BotCard";
import "./BotCollection.css"; // Ensure this import path is correct

// FIX: Added 'onEnlist' to the destructured props
function BotCollection({ bots, onEnlist, onDelete, army }) {
  return (
    <div className="bot-collection">
      <h2>ðŸ¤– Bot Collection</h2>
      <div className="cards-grid">
        {bots.map((bot) => (
          <BotCard
            key={bot.id}
            bot={bot}
            // CRITICAL FIX: Pass the onEnlist handler to the BotCard
            onEnlist={onEnlist} 
            onDelete={onDelete}
            // You can pass the army here to visually disable/style enlisted bots if needed
            isEnlisted={army.some(armyBot => armyBot.id === bot.id)}
          />
        ))}
      </div>
    </div>
  );
}

export default BotCollection;
